 echo $headerTag; ?> class="jm-title <?php echo $params->get('header_class'); ?>"><?php echo $title; ?></<?php echo $headerTag; ?>>		   	<?php endif;?>		    <<?php echo $moduleTag; ?> class="jm-module-content clearfix <?php echo $notitle; ?>">		    	<?php echo $module->content; ?>	      		    </<?php echo $moduleTag; ?>>		</<?php echo $moduleTag; ?>>	</<?php echo $moduleTag; ?>>	<?php endif; ?><?php } ?><?phpfunction modChrome_jmmodule2($module, &$params, &$attribs) {    if($module->showtitle == 0) { $notitle='notitle'; } else $notitle='';$moduleTag      = $params->get('module_tag', 'div');$headerTag      = htmlspecialchars($params->get('header_tag', 'h3'));    $hascenterms = false;$suffix = $params->get('moduleclass_sfx');if (preg_match('/(^|\s)center\-ms/', $suffix)) {    $hascenterms = true;}?><?php if(!$hascenterms) : ?><div class="container<?php echo JMF_TEMPLATE_WIDTH_TYPE ?>"><?php endif; ?>        <<?php echo $moduleTag; ?> class="jm-module clearfix <?php echo $params->get('moduleclass_sfx'); ?>" id="Mod<?php echo $module->id; ?>">            <<?php echo $moduleTag; ?> class="jm-module-in">                <?php if (!$module->showtitle == 0) : ?>                <<?php echo $headerTag; ?> class="jm-title"><?php echo $module->title; ?></<?php echo $headerTag; ?>>                <?php endif; ?>                <?php if($hascenterms) : ?>                <div class="container<?php echo JMF_TEMPLATE_WIDTH_TYPE ?>">                <?php endif; ?>                        <<?php echo $moduleTag; ?> class="jm-module-content clearfix <?php echo $notitle; ?>">                            <<?php echo $moduleTag; ?> class="jm-module-content-in">                            <?php echo $module->content; ?>                            </<?php echo $moduleTag; ?>>                        </<?php echo $moduleTag; ?>>                <?php if($hascenterms) : ?>                </div>                <?php endif; ?>             </<?php echo $moduleTag; ?>>        </<?php echo $moduleTag; ?>><?php if(!$hascenterms) : ?></div><?php endif; ?> <?php }function modChrome_jmmoduleraw($module, &$params, &$attribs) {    $moduleTag      = $params->get('module_tag', 'div');        if ($module->content != '') {?>    <<?php echo $moduleTag; ?> class="jm-module-raw <?php echo $params->get('moduleclass_sfx'); ?>">        <?php echo $module->content; ?>    </<?php echo $moduleTag; ?>><?php     }} ?>